	g_form.setDisplay('paper_notepad_used', false);
	g_form.setDisplay('sla_required', false);
	g_form.setDisplay('sla_tracked', false);
	g_form.setDisplay('system_integration_used', false);
	g_form.setDisplay('tribal_knowledge_used', false);
	g_form.setDisplay('up_cross_sell', false);

	if(g_form.getValue('type') == 'Interaction'){
		g_form.setDisplay('interaction', true);
		g_form.setDisplay('kb_created', true);
		g_form.setDisplay('cti_available', true);
		g_form.setDisplay('call_scripting_used', true);
		g_form.setDisplay('chat_co_browse_used', true);
		g_form.setDisplay('collaboration_tool_used', true);
		g_form.setDisplay('information_readily_available', true);
		g_form.setDisplay('kb_used', true);
		g_form.setDisplay('manual_call_verification', true);
		g_form.setDisplay('manual_email', true);
		g_form.setDisplay('manual_email_internal', true);
		g_form.setDisplay('missing_information', true);
		g_form.setDisplay('paper_documents_used', true);
		g_form.setDisplay('paper_notepad_used', true);
		g_form.setDisplay('sla_required', true);
		g_form.setDisplay('sla_tracked', true);
		g_form.setDisplay('system_integration_used', true);
		g_form.setDisplay('tribal_knowledge_used', true);
		g_form.setDisplay('up_cross_sell', true);
	}
	else if(g_form.getValue('type') == 'Team'){
		g_form.setDisplay('customer_service_strategy', true);
		g_form.setDisplay('case_management_process', true);
		g_form.setDisplay('omni_channel', true);
		g_form.setDisplay('customer_service', true);
		g_form.setDisplay('agent_desktop_search', true);
		g_form.setDisplay('technology_infrastructure', true);
